import axios from "axios";
import dotenv from "dotenv";

const googleKey = process.env.GOOGLE_BOOKS_API_KEY;

const API_BASE_URL = "http://localhost:3000/api";
const googleBookAPI = `https://www.googleapis.com/books/v1/volumes?q=intitle:Discipline+of+a+godly+man+inauthor:Kent+Hughes&key=${googleKey}`;

export const fetchBooks = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/books`);
    return response.data;
  } catch (error) {
    console.error("fetchBooks -> error", error);
  }
};

export const addBook = async (bookData) => {
  try {
    const respose = await axios.post(`${API_BASE_URL}/books`, bookData);

    return respose.data;
  } catch (error) {
    console.error("addBook -> error", error);
    throw error;
  }
};

export const updateBookStatus = async (id, status) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/books/${id}`, {
      read: status,
    });
    return response.data;
  } catch (error) {
    console.error("updateBookStatus -> error", error);
    throw error;
  }
};

export const deleteSelectedBook = async (id) => {
  try {
    const response = await axios.delete(`${API_BASE_URL}/books/${id}`);
    return response.data;
  } catch (error) {
    console.error("deleteBook -> error", error);
    throw error;
  }
};
