import { set } from "lodash";
import { useState } from "react";
import { AddBookForm } from "./AddBookForm";
import { BookList } from "./BookList";
import { Header } from "./Header";
import { FriendList } from "./FriendList";

const initialBooks = [
  {
    title: "The Great Gatsby",
    publishedYear: 1925,
    author: "F. Scott Fitzgerald",
    theme: "Modernist",
  },
  {
    title: "To Kill a Mockingbird",
    publishedYear: 1960,
    author: "Harper Lee",
    theme: "Southern Gothic",
  },
  {
    title: "1984",
    publishedYear: 1949,
    author: "George Orwell",
    theme: "Dystopian",
  },
  {
    title: "Pride and Prejudice",
    publishedYear: 1813,
    author: "Jane Austen",
    theme: "Romantic",
  },
];

const initialFriends = [
  {
    name: "John Doe",
    age: 25,
    booksRead: ["The Great Gatsby", "To Kill a Mockingbird"],
  },
  {
    name: "Jane Doe",
    age: 24,
    booksRead: ["Pride and Prejudice"],
  },
  {
    name: "Paul Doe",
    age: 30,
    booksRead: ["1984", "Mice and Men"],
  },
];

export default function App() {
  const [books, setBooks] = useState(initialBooks);
  const [friends, setFriends] = useState(initialFriends);

  function onAddBook(newBook) {
    setBooks((prevBooks) => [...prevBooks, newBook]);
  }

  return (
    <div className="App">
      <Header />
      <AddBookForm onAddBook={onAddBook} />
      <BookList books={books} />
      <FriendList friends={friends} />
    </div>
  );
}

export function Friend({ name, age, booksRead }) {
  return (
    <div className="friend-card">
      <div className="friend-header">
        <h3 className="friend-name">{name}</h3>
        <span className="friend-age">{age} years old</span>
      </div>
      <div className="friend-books">
        <h4 className="books-read-title">
          Books Read <span className="book-count">({booksRead.length})</span>
        </h4>
        <ul className="books-read-list">
          {booksRead.map((book) => (
            <li key={book} className="book-item">
              <span className="book-icon">ðŸ“š</span>
              {book}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
