import { useState } from "react";

const books = [
  {
    title: "The Great Gatsby",
    publishedYear: 1925,
    author: "F. Scott Fitzgerald",
    cover:
      "https://images-na.ssl-images-amazon.com/images/I/51eZJ6z5QSL._SX322_BO1,204,203,200_.jpg",
  },
  {
    title: "To Kill a Mockingbird",
    publishedYear: 1960,
    author: "Harper Lee",
    cover:
      "https://images-na.ssl-images-amazon.com/images/I/51Vjzj8JNHL._SX322_BO1,204,203,200_.jpg",
  },
  {
    title: "1984",
    publishedYear: 1949,
    author: "George Orwell",
    cover:
      "https://images-na.ssl-images-amazon.com/images/I/41sY4bK3wHL._SX322_BO1,204,203,200_.jpg",
  },
  {
    title: "Pride and Prejudice",
    publishedYear: 1813,
    author: "Jane Austen",
    cover:
      "https://images-na.ssl-images-amazon.com/images/I/51Kz4sZG7SL._SX322_BO1,204,203,200_.jpg",
  },
];

export default function App() {
  return (
    <div className="App">
      <Header />
      <BookList />
    </div>
  );
}

function Header() {
  return (
    <header className="nav-bar">
      <div className="left-nav">
        ðŸ“•
        <h1>ReadTogether</h1>
      </div>

      <div className="right-nav">
        <input
          className="search-bar"
          type="text"
          placeholder="Search books or friends..."
        />
        <p></p>
      </div>
    </header>
  );
}

function BookList() {
  return (
    <div className="book-list">
      <h2>Giap's BookList</h2>
      <ul>
        {books.map((book) => (
          <Book
            title={book.title}
            year={book.publishedYear}
            author={book.author}
          />
        ))}
      </ul>
    </div>
  );
}

function Book({ title, year, author }) {
  const [status, setStatus] = useState("need-to-read");

  return (
    <li>
      <h3>{title}</h3>
      <p>{year}</p>
      <p>{author}</p>
      <select className="book-status-select" value={status}>
        <option value="need-to-read">Haven't Started</option>
        <option value="reading">In Progress</option>
        <option value="done">Finished</option>
      </select>
    </li>
  );
}
