import axios from "axios";

const API_BASE_URL = "http://localhost:3000/api";
const googleKey = process.env.GOOGLE_BOOKS_API_KEY;

export const fetchBooks = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/books`);
    return response.data;
  } catch (error) {
    console.error("fetchBooks -> error", error);
  }
};

export const addBook = async (bookData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/books`, bookData);

    const encodedTitle = encodeURIComponent(bookData.title.replace(/\s/g, "+"));
    const encodedAuthor = encodeURIComponent(
      bookData.author.replace(/\s/g, "+")
    );
    const bookDetails = await axios.get(
      `https://www.googleapis.com/books/v1/volumes?q=intitle:${encodedTitle}inauthor:${encodedAuthor}&key=${googleKey}`
    );

    const firstBook = bookDetails.data.items[0];
    if (firstBook.volumeInfo && firstBook.volumeInfo.imageLinks) {
      const smallThumbnail = firstBook.volumeInfo.imageLinks.smallThumbnail;
      const thumbnail = firstBook.volumeInfo.imageLinks.thumbnail;

      // Use these URLs for displaying the book cover images
      console.log("Small thumbnail:", smallThumbnail);
      console.log("Thumbnail:", thumbnail);
    } else {
      console.log("No image links available for the first book.");
    }

    return response.data;
  } catch (error) {
    console.error("addBook -> error", error);
    throw error;
  }
};

export const updateBookStatus = async (id, status) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/books/${id}`, {
      read: status,
    });
    return response.data;
  } catch (error) {
    console.error("updateBookStatus -> error", error);
    throw error;
  }
};

export const deleteSelectedBook = async (id) => {
  try {
    const response = await axios.delete(`${API_BASE_URL}/books/${id}`);
    return response.data;
  } catch (error) {
    console.error("deleteBook -> error", error);
    throw error;
  }
};
