import "../styles/User.css";

export function User({ name, books }) {
  // This is just sample data - you'll replace with real data later
  const sampleUser = {
    name: "Sarah Johnson",
    avatar: "ðŸ§‘", // You can replace with an actual image later
    booksCount: 12,
    recentBooks: ["The Midnight Library", "Project Hail Mary", "Atomic Habits"],
  };

  return (
    <div className="user-card">
      <div className="user-header">
        <div className="user-avatar">{sampleUser.avatar}</div>
        <div className="user-info">
          <h3 className="user-name">{name}</h3>
          <span className="books-count">{books.length()} books</span>
        </div>
      </div>

      <div className="user-books">
        <h4 className="recent-books-title">Recent Books</h4>
        <ul className="books-list">
          {books.map((book) => (
            <li key={book._id} className="book-item">
              <span className="book-icon">ðŸ“š</span>
              {book.title}
            </li>
          ))}
        </ul>
      </div>

      <div className="user-actions">
        <button className="view-profile">View Profile</button>
        <button className="add-friend">Add Friend</button>
      </div>
    </div>
  );
}
