import { useState } from "react";

export function Book({ title, year, author, theme }) {
  const [status, setStatus] = useState("need-to-read");

  return (
    <li>
      <h3>{title}</h3>
      <h4>{year}</h4>
      <p>{author}</p>
      <p>{theme}</p>
      <select
        className="book-status-select"
        value={status}
        onChange={(e) => setStatus(e.target.value)}
      >
        <option value="need-to-read">Haven't Started</option>
        <option value="reading">In Progress</option>
        <option value="done">Finished</option>
      </select>
    </li>
  );
}
