import { useState, useEffect } from "react";
import { AddBookForm } from "./components/AddBookForm";
import { BookList } from "./components/BookList";
import { Header } from "./components/Header";
import { FriendList } from "./components/FriendList";
import { Home } from "./components/Home";
import { UserList } from "./components/UserList";
import {
  fetchBooks,
  fetchUsers,
  addBook,
  updateBookStatus,
  deleteSelectedBook,
} from "./api";
import "./styles/Index.css";

const initialUsers = [
  {
    id: "user1",
    name: "Giap Do",
    books: [
      {
        _id: "book1",
        title: "1984",
        author: "George Orwell",
        publishedYear: 1949,
        genre: "Dystopian",
        read: 1, // 0 - Not Read, 1 - Reading, 2 - Finished
      },
      {
        _id: "book2",
        title: "Animal Farm",
        author: "George Orwell",
        publishedYear: 1945,
        genre: "Political Satire",
        read: 2,
      },
    ],
    friends: ["user2", "user3"],
  },
  {
    id: "user2",
    name: "Alex Johnson",
    books: [
      {
        _id: "book3",
        title: "To Kill a Mockingbird",
        author: "Harper Lee",
        publishedYear: 1960,
        genre: "Fiction",
        read: 2,
      },
    ],
    friends: ["user1"],
  },
  {
    id: "user3",
    name: "Sophia Liu",
    books: [
      {
        _id: "book4",
        title: "Pride and Prejudice",
        author: "Jane Austen",
        publishedYear: 1813,
        genre: "Romance",
        read: 0,
      },
      {
        _id: "book5",
        title: "The Great Gatsby",
        author: "F. Scott Fitzgerald",
        publishedYear: 1925,
        genre: "Tragedy",
        read: 1,
      },
    ],
    friends: ["user1"],
  },
];

export default function App() {
  const [books, setBooks] = useState([]);
  const [friends, setFriends] = useState([]);
  const [selectStatus, setSelectStatus] = useState("all");
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [name, setName] = useState("");
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const loadBooksandUsers = async () => {
      if (!loggedInUser) {
        setUsers([]);
        setBooks([]);
        return;
      }
      try {
        //fetch user's books
        const { books } = await fetchBooks(loggedInUser);
        setBooks(books);

        //fetch users on the database expect for loggedInUser
        const { users } = await fetchUsers();
      } catch (error) {
        console.error("loadBooks -> error", error);
      }
    };

    loadBooksandUsers();
  }, [loggedInUser]);

  async function onAddBook(newBook) {
    console.log(loggedInUser);
    try {
      const response = await addBook(loggedInUser, newBook);
      console.log("book added");
      setBooks([...books, newBook]);
      return response;
    } catch (error) {
      console.error("onAddBook -> error", error);
    }
  }

  async function handleBookStatusChange(bookId, e) {
    const newState = Number(e.target.value);
    console.log(newState);
    console.log(bookId);

    try {
      await updateBookStatus(loggedInUser, bookId, newState);
      setBooks((prevBooks) =>
        prevBooks.map((book) =>
          book._id === bookId ? { ...book, read: newState } : book
        )
      );
      console.log(newState);
    } catch (error) {
      console.error("handleBookStatusChange -> error", error);
    }
  }

  function handleFilterBooks(e) {
    setSelectStatus(e.target.value);
  }

  async function handleDeleteBook(id) {
    try {
      await deleteSelectedBook(id);
      setBooks((prevBooks) => prevBooks.filter((book) => book._id !== id));
    } catch (error) {
      console.error("handleDeleteBook -> error", error);
    }
  }

  function onLoggedIn(id, fullname) {
    setLoggedInUser(id);
    setName(fullname);
  }

  function onLogout() {
    setLoggedInUser(null);
  }

  return (
    <div className="App">
      {loggedInUser ? (
        <>
          <Header onLogout={onLogout} />
          <AddBookForm onAddBook={onAddBook} />
          <BookList
            books={books}
            status={selectStatus}
            onBookStatusChange={handleBookStatusChange}
            onFilterBooks={handleFilterBooks}
            onDeleteBook={handleDeleteBook}
            loggedInUser={name}
          />
          <FriendList friends={friends} loggedInUser={name} />
          <UserList users={initialUsers} />
        </>
      ) : (
        <Home loggedIn={onLoggedIn} />
      )}
    </div>
  );
}
