import { set } from "lodash";
import { useState } from "react";
import { AddBookForm } from "./AddBookForm";

const initialBooks = [
  {
    title: "The Great Gatsby",
    publishedYear: 1925,
    author: "F. Scott Fitzgerald",
  },
  {
    title: "To Kill a Mockingbird",
    publishedYear: 1960,
    author: "Harper Lee",
  },
  {
    title: "1984",
    publishedYear: 1949,
    author: "George Orwell",
  },
  {
    title: "Pride and Prejudice",
    publishedYear: 1813,
    author: "Jane Austen",
  },
];

export default function App() {
  const [books, setBooks] = useState(initialBooks);

  function onAddBook(newBook) {
    setBooks((prevBooks) => [...prevBooks, newBook]);
  }

  return (
    <div className="App">
      <Header />
      <AddBookForm onAddBook={onAddBook} />
      <BookList books={books} />
    </div>
  );
}

function Header() {
  return (
    <header className="nav-bar">
      <div className="left-nav">
        ðŸ“•
        <h1>ReadTogether</h1>
      </div>

      <div className="right-nav">
        <input
          className="search-bar"
          type="text"
          placeholder="Search books or friends..."
        />
        <p></p>
      </div>
    </header>
  );
}

function BookList({ books }) {
  return (
    <div className="book-list">
      <h2>Giap's BookList</h2>
      <ul>
        {books.map((book) => (
          <Book
            key={book.title}
            title={book.title}
            year={book.publishedYear}
            author={book.author}
          />
        ))}
      </ul>
    </div>
  );
}

function Book({ title, year, author }) {
  const [status, setStatus] = useState("need-to-read");

  return (
    <li>
      <h3>{title}</h3>
      <p>{year}</p>
      <p>{author}</p>
      <select
        className="book-status-select"
        value={status}
        onChange={(e) => setStatus(e.target.value)}
      >
        <option value="need-to-read">Haven't Started</option>
        <option value="reading">In Progress</option>
        <option value="done">Finished</option>
      </select>
    </li>
  );
}
