import { set } from "lodash";
import { useState } from "react";
import { AddBookForm } from "./AddBookForm";
import { BookList } from "./BookList";
import { Header } from "./Header";

const initialBooks = [
  {
    title: "The Great Gatsby",
    publishedYear: 1925,
    author: "F. Scott Fitzgerald",
    theme: "Modernist",
  },
  {
    title: "To Kill a Mockingbird",
    publishedYear: 1960,
    author: "Harper Lee",
    theme: "Southern Gothic",
  },
  {
    title: "1984",
    publishedYear: 1949,
    author: "George Orwell",
    theme: "Dystopian",
  },
  {
    title: "Pride and Prejudice",
    publishedYear: 1813,
    author: "Jane Austen",
    theme: "Romantic",
  },
];

const initialFriends = [
  {
    name: "John Doe",
    age: 25,
    booksRead: ["The Great Gatsby", "To Kill a Mockingbird"],
  },
  {
    name: "Jane Doe",
    age: 24,
    booksRead: ["Pride and Prejudice"],
  },
  {
    name: "Paul Doe",
    age: 30,
    booksRead: ["1984", "Mice and Men"],
  },
];

export default function App() {
  const [books, setBooks] = useState(initialBooks);
  const [friends, setFriends] = useState(initialFriends);

  function onAddBook(newBook) {
    setBooks((prevBooks) => [...prevBooks, newBook]);
  }

  return (
    <div className="App">
      <Header />
      <AddBookForm onAddBook={onAddBook} />
      <BookList books={books} />
      <FriendList friends={friends} />
    </div>
  );
}

function FriendList({ friends }) {
  return (
    <div className="friend-list">
      <h2>Giap's Reading Friends</h2>
      <ul>
        {friends.map((friend) => (
          <Friend
            name={friend.name}
            age={friend.age}
            booksRead={friend.booksRead}
          />
        ))}
      </ul>
    </div>
  );
}

function Friend({ name, age, booksRead }) {
  return (
    <li>
      <h3>{name}</h3>
      <p>{age}</p>
      <ul>
        {booksRead.map((book) => (
          <li>{book}</li>
        ))}
      </ul>
    </li>
  );
}
